import React, { useState } from 'react'
import axios from "axios";
import Navigate from "react-router-dom";
const Login = () => {

    const [isLoggedIn , setIsLoggedIn] = useState(false);
    const [logindata , setLoginData] = useState({
        username : "",
        password : ""
    });

    function changeHandler(event) {
        
        setLoginData(prevLoginData => {
            return{
                ...prevLoginData,
                [event.target.name] : event.target.value
            }
        })
    }

    const handleLoginSubmit = async (e) => {
        e.preventDefault();
        const {username, password} = logindata;

        try{
            if(username && password){
                await axios.post("http://localhost:8081/login" , logindata)
                .then( (res) => {
                    console.log(res)
                    setIsLoggedIn(true);
                });
            
            }else{
                //console.log("err")
                alert("please enter valid input")
            }

            if(isLoggedIn) {
                return <Redirect to="/home" />;
            }


        }catch(e){
            console.log(e);
        }
    }
  return (
    <div>
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLoginSubmit}>          

                <label>Username :</label>
                <input className='' type='text' placeholder='' name='username' value={logindata.username} onChange={changeHandler} />

                <label>Password :</label>
                <input className='' type='password' placeholder='' name='password' value={logindata.password} onChange={changeHandler} />
                
                <button type='submit' name='submit'>Submit</button>
            </form>
        </div>
    </div>
  )
}

export default Login
