import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
    const [formData, setFormData] = useState({
        fullname: '',
        username: '',
        email: '',
        contact: '',
        password: ''
    });

    const changeHandler = (event) => {
        setFormData(prevFormData => ({
            ...prevFormData,
            [event.target.name]: event.target.value
        }));
    };

    const handleRegisterSubmit = async (e) => {
        e.preventDefault();

        const { fullname, username, email, contact, password } = formData;

        try {
            if (fullname && username && email && contact && password) {
                const response = await axios.post('http://localhost:8081/register', formData);
                console.log(response);
                alert('Registration Successful!');
            } else {
                alert('Please enter valid details');
            }
        } catch (error) {
            console.log(error);
            alert('Error during registration');
        }
    };

    return (
        <div>
            <h1>Create Your Account</h1>
            <form onSubmit={handleRegisterSubmit}>
                <label>FullName :</label>
                <input type='text' name='fullname' value={formData.fullname} onChange={changeHandler} />

                <label>Username :</label>
                <input type='text' name='username' value={formData.username} onChange={changeHandler} />

                <label>Email :</label>
                <input type='email' name='email' value={formData.email} onChange={changeHandler} />

                <label>Phone No :</label>
                <input type='number' name='contact' value={formData.contact} onChange={changeHandler} />

                <label>Password :</label>
                <input type='password' name='password' value={formData.password} onChange={changeHandler} />

                <button type='submit'>Submit</button>
            </form>
        </div>
    );
};

export default Register;
