import axios from 'axios';
import React, { useEffect, useState } from 'react';

const Home = () => {
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    fetchUser();
  }, []);

  useEffect(() => {
    console.log(userData); // Log updated state value
  }, [userData]);

  const fetchUser = () => {
    const token = localStorage.getItem('authToken'); // Retrieve authentication token from local storage
    axios.get("http://localhost:8081/user", { headers: { Authorization: `Bearer ${token}` } })
      .then(res => {
        setUserData(res.data); // Set state with response data directly
      })
      .catch(error => {
        console.error("Error fetching user data:", error);
      });
  }
  

  return (
    <div>
      {/* Display user data if available */}
      {userData && (
        <div>
          <h1>{userData.username}</h1>
          <p>{userData.email}</p>
          {/* Add other user data fields as needed */}
        </div>
      )}
    </div>
  );
};

export default Home;
