import React, { useState } from 'react'
import axios from "axios";

const Register = () => {

    const [formdata , setFormData] = useState({
        fullname : "", 
        username : "",
        email : "",
        contact: "",
        password : ""
    });

    function changeHandler (event) { 
        setFormData( prevFormData => {
            return {
                ...prevFormData,
                [event.target.name] : event.target.value
            }
        })
    }

    const handleRegisterSubmit = async (e) => {
        e.preventDefault();

        const { fullname, username, email, contact , password } = formdata;
        try{

            if(fullname && username && email && contact && password){
                axios.post("http://localhost:8081/register" ,formdata )
                //.then(res => console.log(res))
            }else{
                alert("Please Enter Valid Details!!")
            }

        }catch(error){
            console.log(error);
        }
    }
  return (
    <div>
        <div>
            <h1>Create Your Account</h1>
            <form onSubmit={handleRegisterSubmit}>
                <label>FullName :</label>
                <input className='' type='text' placeholder='' name='fullname' value={formdata.fullname} onChange={changeHandler} />

                <label>Username :</label>
                <input className='' type='text' placeholder='' name='username' value={formdata.username} onChange={changeHandler} />

                <label>Email :</label>
                <input className='' type='email' placeholder='' name='email' value={formdata.email} onChange={changeHandler} />

                <label>Phone No :</label>
                <input className='' type='number' placeholder='' name='contact' value={formdata.contact} onChange={changeHandler} />

                <label>Password :</label>
                <input className='' type='password' placeholder='' name='password' value={formdata.password} onChange={changeHandler} />

                <button type='submit' name='submit'>Submit</button>
            </form>
        </div>
    </div>
  )
}

export default Register
