import React, { useState } from 'react'
import axios from "axios";
import {useNavigate} from "react-router-dom";

const Login = () => {

    
    const [logindata , setLoginData] = useState({
        username : "",
        password : ""
    });

    const navigate = useNavigate()

    function changeHandler(event) {
        
        setLoginData(prevLoginData => {
            return{
                ...prevLoginData,
                [event.target.name] : event.target.value
            }
        })
    }

    const handleLoginSubmit = async (e) => {
        e.preventDefault();
        const {username, password} = logindata;

        try{
            if(username && password){
                const logged = await axios.post("http://localhost:8081/login" , logindata)
                .then( (res) => {
                    console.log(res.data.user.username ); 
                    console.log(logindata.username);
                    // if( res.data.user.username === logindata.username){
                    //     navigate("/home")
                    // } else{
                    //     alert("Invalid details")
                    // }                  
                     
                });
                // setIsLoggedIn(true);
            
            }else{
                //console.log("err")
                alert("please enter valid input")
            }

            // if(isLoggedIn){
            //     navigate("/home")
            // }


        }catch(e){
            console.log(e);
        }
    }
  return (
    <div>
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLoginSubmit}>          

                <label>Username :</label>
                <input className='' type='text' placeholder='' name='username' value={logindata.username} onChange={changeHandler} />

                <label>Password :</label>
                <input className='' type='password' placeholder='' name='password' value={logindata.password} onChange={changeHandler} />
                
                <button type='submit' name='submit'>Submit</button>
            </form>
        </div>
    </div>
  )
}

export default Login
