import { useState } from "react";
import "./App.css";

function App() {
  const[count, setCount] = useState(0);
  function decreseHandler(){

    setCount(count-1);

  }
  function increseHandler(){

    setCount(0);

  }

  function resetHandler(){

    setCount(count+1);

  }
  return (
    <div className="w-[100vw] h-[100vh] flex justify-center items-center bg-[#343434] flex-col gap-10">
        <div className="text-[#0398d4] font-medium text-2xl ">Increment & Decrement</div>
        <div className="bg-white flex justify-center gap-12 py-3 rounded-sm text-[25px] text-[#343151]">
          <button onClick={decreseHandler} className="border-r-2 text-center w-20 border-[#bfbfbf] text-5xl">-</button>

          <div className="font-bold gap-12 text-5xl">{count}</div>

          <button onClick={increseHandler} className="border-r-2 text-center w-20 border-[#bfbfbf] text-5xl">+</button>
        </div>

        <button onClick={resetHandler} className="bg-[#0398d4] text-white px-5 py-2">Reset</button>
    </div>
  );
}

export default App;
